  var ViewBuilder = require('./ViewBuilder')

  var NATIVE_BRIDGE_NAME = "GetSocialChatCordova";

  var ChatListViewBuilder = function() {
  }

  ChatListViewBuilder.construct = function() {
    var item = new ChatListViewBuilder();
    return item;
  }

    ChatListViewBuilder.prototype.show = function() {
      var option = this.toJson();

      cordova.exec(null, null, NATIVE_BRIDGE_NAME, "createChatListView", [option]);
    }

    ChatListViewBuilder.prototype.close = function() {
      var option = {}
      cordova.exec(null, null, NATIVE_BRIDGE_NAME, "closeChatView", [option]);
    }

  ChatListViewBuilder.prototype.__proto__ = ViewBuilder.prototype;

  module.exports = ChatListViewBuilder;
